module Fleur(){   
<code >echo("n=",n);  
echo("c=",c);  
echo("E=",E);     
echo("P=",P);  

petale();  
centre();  
queue();  
feuille();  
mirror([0,1,0])feuille();  </code>
}  
module petale(){<br />
difference(){<br />
for (i=[1:n]){<br />
rotate([0,-2<em >E,i</em>(360/n)]){<br />
resize([0,(P/2),E], auto=[false,false,false]){<br />
translate([c/2+P/3,0,0]) sphere(r=P/2);<br />
}<br />
}<br />
echo(&quot;petal n° &quot;,i,&quot;/&quot;,n);<br />
}<br />
translate([0,0,-(4<em >P+4</em>c)/2]) cube((4<em >P)+(4</em>c), center=true);<br />
}<br />
echo(&quot;petals done&quot;);<br />
}  
module centre(){<br />
resize([0,0,E/2]){<br />
difference(){<br />
sphere(r=c/2);<br />
translate([0,0,-c/2]) cube(c, center=true);<br />
}<br />
}<br />
echo(&quot;le centre est fait&quot;);<br />
}  
module queue(){<br />
translate([(P+c),0,0]){<br />
rotate([90,180,90]){<br />
linear_extrude(height=(P+c)<em >1.5,center =true, convexity=10, scale=2){<br />
difference(){<br />
circle(r=E/2, center=true);<br />
translate([-E,0,0]) square([2</em>E,2*E]);<br />
}<br />
}<br />
}<br />
}<br />
}  
module feuille(){<br />
translate([P+c<em >2,0,0]){<br />
rotate([0,-2</em>E,180-P-c]){<br />
scale([2,0.5,1]){<br />
difference(){<br />
minkowski(){<br />
cylinder(r=P-n,h=E/2);<br />
translate([P-n,0,0]) sphere(r=E/2);<br />
}<br />
translate([P-n,0,-E])cylinder(r=3*E, h=E);<br />
}<br />
}<br />
}<br />
}<br />
