//+-------------------------------------------------------------+<br />
//|                 OPENSCAD BAMBOO/PIPE CAP                    |<br />
//|                    2014 <a data-cfemail="523533283b373e12353f333b3e7c313d3f" href="https://www.thingiverse.com/cdn-cgi/l/email-protection" class="__cf_email__" >[email&#160;protected]</a>                    |<br />
//|remix from &quot;Cap For Bamboo&quot; by mitsuhito, published   10:21:49|<br />
//|     See <a rel="nofollow" href="http://www.thingiverse.com/thing:373001" >http://www.thingiverse.com/thing:373001</a>             |<br />
//+-------------------------------------------------------------+<br />
//Easy to use with a caliper :<br />
internal_D=35; //internal diameter of the bamboo<br />
external_D=40; // external diameter of the bamboo<br />
Ep=3;               //thickness you want (not the thickness of the bamboo !!)<br />
Height=10;      //height of the cap (without the bight)<br />
$fs=0.5;<br />
$fa=0.5;<br />
difference(){<br />
union(){<br />
//external<br />
difference(){<br />
cylinder(r=(external_D/2)+Ep,h=Height);<br />
translate([0,0,-1])cylinder(r=external_D/2,h=Height+2);<br />
}<br />
//internal<br />
difference(){<br />
cylinder(r=(internal_D/2),h=Height);<br />
translate([0,0,-1])cylinder(r=internal_D/2-Ep,h=Height+2);<br />
}<br />
//top<br />
translate([0,0,Height-Ep])cylinder(r=(external_D/2)+Ep/2,h=Ep);<br />
}<br />
translate([0,(external_D+4<em >Ep)/2,Height/2])rotate([90,0,0])cylinder(r=Ep/2, h=external_D+4</em>Ep);<br />
}<br />
//bight<br />
difference(){<br />
union(){<br />
translate([-Height/4,-Height/4,Height])cube([Height/2,Height/2,Height/2]);<br />
translate([0,Height/4,Height+Height/2])rotate([90,0,0])cylinder(r=Height/4, h=Height/2 );<br />
translate([0,0,Height])cylinder(r1=Height/2+Ep,r2=Height/4,h=Ep, center=true);<br />
}<br />
translate([0,Height/4+1,Height+Height/2])rotate([90,0,0])cylinder(r=Height/8, h=Height );<br />
}
