Edit in Opencsad for personnalisation  
//+-------------------------------------------------------------+<br />
//|                 OPENSCAD PIGGY MUG BANK - Tasselire         |<br />
//|                    2014 <a data-cfemail="2146405b48444d61464c40484d0f424e4c" href="https://www.thingiverse.com/cdn-cgi/l/email-protection" class="__cf_email__" >[email&#160;protected]</a>                    |<br />
//+-------------------------------------------------------------+  
//standart mug cotation from <a rel="nofollow" href="http://blog.ampli.fi/the-standard-coffee-mug-dimensions/" >http://blog.ampli.fi/the-standard-coffee-mug-dimensions/</a>:<br />
//Outer diameter (a): 82mm<br />
//Outer diameter + handle length (b): 120mm<br />
//Height (c): 95mm<br />
//Handle height (d): 73mm<br />
//Wall thickness (e): 6mm (tapers down at the top of the mug)  
//Easy use with a caliper :<br />
external_D=82; // external diameter<br />
internal_D=external_D-8; //Standart internal diameter<br />
Ep=3;               //thickness you want (not the thickness of the Mug !!)<br />
Height=40;      //height of the cap   
$fs=0.2;<br />
$fa=0.2;<br />
translate([0,0,Height]) rotate([0,180,])cap();<br />
module cap(){<br />
difference(){<br />
union(){<br />
//external<br />
difference(){<br />
cylinder(r1=(external_D/2)+1,r=(external_D/2)+Ep,h=Height-Ep);<br />
translate([0,0,-1])cylinder(r=external_D/2,h=Height+2);<br />
}<br />
//internal<br />
difference(){<br />
cylinder(r=(internal_D/2),h=Height);<br />
translate([0,0,-1])cylinder(r=internal_D/2-Ep,h=Height+2);<br />
}<br />
//top<br />
translate([0,0,Height-Ep])cylinder(r1=(external_D/2)+Ep,r2=(internal_D/2)+Ep/2,h=Ep);<br />
}  
<code >    //coin hole  
    cube([30,4,5*Height], center=true);   

    //handle hole  
    union(){  
        translate([-10,0,-1])cube([20,2*external_D,Height-Ep-19]);  
        translate([0,(external_D+internal_D)/2,Height-Ep-20])rotate([90,0,0])cylinder(r=10, h=external_D+4*Ep);  
    }  

}  </code>
}
