void printDigits(int digits){<br />
// utility function for digital clock display: prints preceding colon and leading 0<br />
Serial.print(&quot;:&quot;);<br />
if(digits &lt; 10)<br />
Serial.print('0');<br />
Serial.print(digits);<br />
}  
void processSyncMessage() {<br />
unsigned long pctime;<br />
const unsigned long DEFAULT_TIME = 1357041600; // Jan 1 2013  
if(Serial.find(TIME_HEADER)) {<br />
pctime = Serial.parseInt();<br />
if( pctime &gt;= DEFAULT_TIME) { // check the integer is a valid time (greater than Jan 1 2013)<br />
setTime(pctime); // Sync Arduino clock to the time received on the serial port<br />
}<br />
}  
}  
time_t requestSync()<br />
{<br />
Serial.write(TIME_REQUEST);<br />
return 0; // the time will be sent later in response to serial mesg<br />
}  
