/**  

<li >SyncArduinoClock.   </li>
<li >
</li>
<li >portIndex must be set to the port connected to the Arduino  </li>
<li >
</li>
<li >The current time is sent in response to request message from Arduino   </li>
<li >or by clicking the display window   </li>
<li >
</li>
<li >The time message is 11 ASCII text characters; a header (the letter 'T')  </li>
<li >followed by the ten digit system time (unix time)<br />
*/  </li>

import processing.serial.*;<br />
import java.util.Date;<br />
import java.util.Calendar;<br />
import java.util.GregorianCalendar;  
public static final short portIndex = 0;  // select the com port, 0 is the first port<br />
public static final String TIME_HEADER = &quot;T&quot;; //header for arduino serial time message<br />
public static final char TIME_REQUEST = 7;  // ASCII bell character<br />
public static final char LF = 10;     // ASCII linefeed<br />
public static final char CR = 13;     // ASCII linefeed<br />
Serial myPort;     // Create object from Serial class  
void setup() {<br />
size(200, 200);<br />
println(Serial.list());<br />
println(&quot; Connecting to -&gt; &quot; + Serial.list()[portIndex]);<br />
myPort = new Serial(this,Serial.list()[portIndex], 9600);<br />
println(getTimeNow());<br />
}  
void draw()<br />
{<br />
textSize(20);<br />
textAlign(CENTER);<br />
fill(0);<br />
text(&quot;Click to send\nTime Sync&quot;, 0, 75, 200, 175);<br />
if ( myPort.available() &gt; 0) {  // If data is available,<br />
char val = char(myPort.read());         // read it and store it in val<br />
if(val == TIME_REQUEST){<br />
long t = getTimeNow();<br />
sendTimeMessage(TIME_HEADER, t);<br />
}<br />
else<br />
{<br />
if(val == LF)<br />
; //igonore<br />
else if(val == CR)<br />
println();<br />
else<br />
print(val); // echo everying but time request<br />
}<br />
}<br />
}  
void mousePressed() {<br />
sendTimeMessage( TIME_HEADER, getTimeNow());<br />
}  
void sendTimeMessage(String header, long time) {<br />
String timeStr = String.valueOf(time);<br />
myPort.write(header);  // send header and time to arduino<br />
myPort.write(timeStr);<br />
myPort.write('\n');<br />
}  
long getTimeNow(){<br />
// java time is in ms, we want secs<br />
Date d = new Date();<br />
Calendar cal = new GregorianCalendar();<br />
long current = d.getTime()/1000;<br />
long timezone = cal.get(cal.ZONE_OFFSET)/1000;<br />
long daylight = cal.get(cal.DST_OFFSET)/1000;<br />
return current + timezone + daylight;<br />
}
