// the MIDI channel number to send messages<br />
const int channel = 1;  
// the MIDI continuous controller for each analog input<br />
const int controllerA0 = 10; // 10 = pan position<br />
const int controllerA1 = 11; // 11 = volume/expression<br />
const int controllerA2 = 91; // 91 = reverb level<br />
const int controllerA3 = 93; // 93 = chorus level  
void setup() {<br />
}  
// store previously sent values, to detect changes<br />
int previousA0 = -1;<br />
int previousA1 = -1;<br />
int previousA2 = -1;<br />
int previousA3 = -1;  
elapsedMillis msec = 0;  
void loop() {<br />
// only check the analog inputs 50 times per second,<br />
// to prevent a flood of MIDI messages<br />
if (msec &gt;= 20) {<br />
msec = 0;<br />
int n0 = analogRead(A0) / 8;<br />
int n1 = analogRead(A1) / 8;<br />
int n2 = analogRead(A2) / 8;<br />
int n3 = analogRead(A3) / 8;<br />
// only transmit MIDI messages if analog input changed<br />
if (n0 != previousA0) {<br />
usbMIDI.sendControlChange(controllerA0, n0, channel);<br />
previousA0 = n0;<br />
}<br />
if (n1 != previousA1) {<br />
usbMIDI.sendControlChange(controllerA1, n1, channel);<br />
previousA1 = n1;<br />
}<br />
if (n2 != previousA2) {<br />
usbMIDI.sendControlChange(controllerA2, n2, channel);<br />
previousA2 = n2;<br />
}<br />
if (n3 != previousA3) {<br />
usbMIDI.sendControlChange(controllerA3, n3, channel);<br />
previousA3 = n3;<br />
}<br />
}  
// MIDI Controllers should discard incoming MIDI messages.<br />
// <a rel="nofollow" href="http://forum.pjrc.com/threads/24179-Teensy-3-Ableton-Analog-CC-causes-midi-crash" >http://forum.pjrc.com/threads/24179-Teensy-3-Ableton-Analog-CC-causes-midi-crash</a><br />
while (usbMIDI.read()) {<br />
// ignore incoming messages<br />
}<br />
}  
