function main(args) {<br />
var baseFile = args[0];<br />
var grid = load(baseFile,0.075<em >MM);<br />
var intersect = new Intersection();<br />
intersect.add(new DataSourceGrid(grid, 255));<br />
intersect.add(new VolumePatterns.Gyroid(10</em>MM, 1*MM));  
<code >var maker = new GridMaker();  
maker.setSource(intersect);  

var dest = createGrid(grid);  
maker.makeGrid(dest);  

return dest;  </code>
