$fn = 100;  
tamper_radius = 37.5;<br />
tamper_height = 10;<br />
holder_radius = 10;<br />
holder_height = 50;  
<code >cylinder(r = tamper_radius, h = tamper_height);  
cylinder(r = holder_radius, h = holder_height);  
translate([0,0,holder_height]){  
    sphere(holder_radius*2);  
}  

difference(){  
    translate([0,0,tamper_height/2]){  
    sphere(holder_radius*2);  
    }  
    translate([0,0,-holder_radius*2+tamper_height/2]){  
        cylinder(h = holder_radius*3,r = tamper_radius,center=true);  
    }  
}  </code>
