description.text:
use openScad and touch the datalove :<br />
comment or uncomment the part/module you need   
Savonius_Diametre_Base=100;<br />
Thinkness=4;  
Roller_Diametre_Externe=22;<br />
Roller_Diametre_Interne=8;<br />
Roller_Hauteur=7;  
Motor_Diametre_Central=13;<br />
Motor_Diametre_Holder=3;<br />
Motor_Espacement_Holder=25;  
footer_height=50;  
//Arm1();  
//Arm2();  
//savonius();  
//poulie();  
//footer();  
full_animated();  
module full_animated(){<br />
Arm1();<br />
color(&quot;red&quot;)translate([Savonius_Diametre_Base/2,0,Thinkness])rotate([0,0,90])Arm2();<br />
translate([0,0,Thinkness+Roller_Hauteur-1])rotate([0,0,$t<em >360])savonius();<br />
translate([Savonius_Diametre_Base/2,0,Thinkness])rotate([0,0,90])translate([Savonius_Diametre_Base/2,0,-10</em>epaisseur])dumymotor();<br />
translate([Savonius_Diametre_Base/2,0,Thinkness])rotate([180,0,90])translate([Savonius_Diametre_Base/2,0,-5<em >epaisseur])poulie();<br />
color(&quot;green&quot;)translate([Savonius_Diametre_Base/4,0,-2</em>Thinkness])footer();<br />
}  
module Arm1(){<br />
difference(){<br />
union(){<br />
hull(){<br />
cylinder(r=(Roller_Diametre_Externe/2)+Thinkness*2, h=Thinkness);<br />
translate([Savonius_Diametre_Base/2,0,0])cylinder(r=Roller_Diametre_Externe/2, h=Thinkness);<br />
}<br />
translate([0,0,Thinkness])cylinder(r=(Roller_Diametre_Externe/2)+Thinkness, h=Roller_Hauteur-1);<br />
}<br />
cylinder(r=Roller_Diametre_Interne/2+1, h=Thinkness+Roller_Hauteur);<br />
translate([0,0,Thinkness])cylinder(r=Roller_Diametre_Externe/2, h=Roller_Hauteur);<br />
translate([Savonius_Diametre_Base/2,0,0])cylinder(r=Thinkness/2, h=Thinkness+1);<br />
//4 holes for a base<br />
translate([(Savonius_Diametre_Base/4)-Thinkness,Thinkness,0])cylinder(r=Thinkness/4, h=Thinkness+1);<br />
translate([(Savonius_Diametre_Base/4)-Thinkness,-Thinkness,0])cylinder(r=Thinkness/4, h=Thinkness+1);<br />
translate([(Savonius_Diametre_Base/4)+Thinkness,Thinkness,0])cylinder(r=Thinkness/4, h=Thinkness+1);<br />
translate([(Savonius_Diametre_Base/4)+Thinkness,-Thinkness,0])cylinder(r=Thinkness/4, h=Thinkness+1);  
<code >}  </code>
}  
module Arm2(){  
<code >difference(){  
    hull(){  
        cylinder(r=Roller_Diametre_Externe/2, h=Thinkness);  
        translate([Savonius_Diametre_Base/2,0,0])cylinder(r=(Motor_Espacement_Holder/2 +Motor_Diametre_Holder+Thinkness/2), h=Thinkness);  
    }  
cylinder(r=Thinkness/2, h=Thinkness*3);  
translate([Savonius_Diametre_Base/2,0,0])cylinder(r=Motor_Diametre_Central/2, h=Thinkness+1);  
translate([Savonius_Diametre_Base/2,Motor_Espacement_Holder/2,0])cylinder(r=Motor_Diametre_Holder/2, h=Thinkness+1);  
translate([Savonius_Diametre_Base/2,-Motor_Espacement_Holder/2,0])cylinder(r=Motor_Diametre_Holder/2, h=Thinkness+1);  
}  </code>
}  
//+----------------------------------------------------------+<br />
//|                 OPENSCAD SAVONIUS ROTOR                  |<br />
//|                    2014 <a data-cfemail="d8bfb9a2b1bdb498bfb5b9b1b4f6bbb7b5" href="https://www.thingiverse.com/cdn-cgi/l/email-protection" class="__cf_email__" >[email&#160;protected]</a>                 |<br />
//|see <a rel="nofollow" href="http://fr.wikipedia.org/wiki/Rotor_de_Savonius" >http://fr.wikipedia.org/wiki/Rotor_de_Savonius</a>        |<br />
//|or <a rel="nofollow" href="http://en.wikipedia.org/wiki/Savonius_wind_turbine" >http://en.wikipedia.org/wiki/Savonius_wind_turbine</a>     |<br />
//+----------------------------------------------------------+  
Diametre_base=100;                                      //base diameter (D)<br />
ecart=16;                                                       //central Gap - ecart (e)<br />
epaisseur=3;                                                    //thinkness ( base and max'wing)<br />
gorge=3;<br />
hauteur=100;                                                    //height<br />
diametre_pale=(Diametre_base/2)+(ecart/2); //wing diameter  
//try to get 0.166666666666666<br />
echo(&quot;best : 0.15 &lt; &quot;, ecart/Diametre_base ,&quot;&lt; 0.17&quot;);  
Twist=0;                                        //Wings of Fury<br />
n_pale=2;                                       //number of wings -  
$fa=1;<br />
$fs=1;  
module savonius(){<br />
base();<br />
rotor2();<br />
}  
module rotor2(){<br />
for (i=[1:n_pale]){<br />
color([0.5+sin(10<em >i)/2,0,1/i]){<br />
rotate([0,0,i</em>(360/n_pale)]){<br />
translate([-diametre_pale/2+ecart/2,0,epaisseur+gorge]){<br />
linear_extrude(height=hauteur+epaisseur,convexity=100,twist=Twist){<br />
difference(){<br />
circle(r=diametre_pale/2);<br />
circle(r=(diametre_pale/2)-epaisseur/2);<br />
translate([-Diametre_base,0,0])square(Diametre_base*2);<br />
}<br />
}<br />
}<br />
}<br />
}<br />
}<br />
}  
module base(){<br />
difference(){<br />
color(&quot;CadetBlue&quot;){<br />
union(){<br />
cylinder(r=Diametre_base/2, h=epaisseur);<br />
translate([0,0,epaisseur])cylinder(r=(Diametre_base-epaisseur)/2, h=gorge);<br />
translate([0,0,epaisseur+gorge])cylinder(r=Diametre_base/2, h=epaisseur);<br />
}<br />
}<br />
translate([0,0,-epaisseur]) cylinder(r=1, h=3*10); //change r as your preference<br />
}  
}  
module dumymotor(){<br />
color(&quot;grey&quot;)cylinder(r=Motor_Espacement_Holder/2+epaisseur,h=10<em >epaisseur);<br />
color(&quot;black&quot;)cylinder(r=Motor_Diametre_Holder/2,h=13</em>epaisseur);<br />
}  
module poulie(){<br />
difference(){<br />
color(&quot;Blue&quot;){<br />
union(){<br />
cylinder(r=Motor_Diametre_Central/2, h=epaisseur);<br />
translate([0,0,epaisseur])cylinder(r=(Motor_Diametre_Central-epaisseur)/2, h=gorge);<br />
translate([0,0,epaisseur+gorge])cylinder(r=Motor_Diametre_Central/2, h=epaisseur);<br />
cylinder(r=(Motor_Diametre_Central-epaisseur)/2, h=(gorge+epaisseur<em >2)</em>2);  
<code >            }  
        }  
        translate([0,0,-epaisseur]) cylinder(r=0.5, h=100); //change r=3 as your preference  
    }  </code>
}  
module footer(){<br />
difference(){<br />
hull(){<br />
cube(4<em >Thinkness,center=true);<br />
translate([-0,0,-(2</em>Thinkness+footer_height)])cylinder(r=Savonius_Diametre_Base/6,h=Thinkness);<br />
}<br />
translate([-Thinkness,Thinkness,Thinkness])cylinder(r=Thinkness/4, h=Thinkness+1);<br />
translate([-Thinkness,-Thinkness,Thinkness])cylinder(r=Thinkness/4, h=Thinkness+1);<br />
translate([+Thinkness,Thinkness,Thinkness])cylinder(r=Thinkness/4, h=Thinkness+1);<br />
translate([+Thinkness,-Thinkness,Thinkness])cylinder(r=Thinkness/4, h=Thinkness+1);<br />
}<br />
translate([-0,0,-(2*Thinkness+footer_height)])cylinder(r=Savonius_Diametre_Base/4,h=Thinkness);  
}
