//Bottlelift by gaziel for pierrelesek, Alnoa  
Bottle_X=4; //how many bottle in 1 line ?  
Bottle_Y=1; // how many line of beer ?  
Magnet_diameter=20; // caliper time !  
Thinkness=2;<br />
taillemodule=62;//62 for a standart 33cl beer bottle  
module OneBelgianBeerPlease(){  
<code >cube([taillemodule,taillemodule,2]);  
    translate([taillemodule/2,taillemodule/2,3]){  
        difference(){  
            cylinder(d1=Magnet_diameter+Thinkness*2,d2=Magnet_diameter+Thinkness,h=Thinkness,center=true);  
            cylinder(d=Magnet_diameter,h=Thinkness,center=true);  

        }  
    }  </code>
}  
for(j=[0,Bottle_Y-1]){<br />
for (i=[0:Bottle_X-1]){<br />
translate([i<em >taillemodule,j</em>taillemodule,0])OneBelgianBeerPlease();<br />
}<br />
}

