//Fahrenheit para Celsius<br />
double Fahrenheit(double celsius)<br />
{<br />
return 1.8 * celsius + 32;<br />
}  
//Celsius para Kelvin<br />
double Kelvin(double celsius)<br />
{<br />
return celsius + 273.15;<br />
}<br />
// Algoritimo inteiramente copiado, créditos:<br />
// dewPoint function NOAA<br />
// reference: <a rel="nofollow" href="http://wahiduddin.net/calc/density_algorithms.htm" >http://wahiduddin.net/calc/density_algorithms.htm</a><br />
double dewPoint(double celsius, double humidity)<br />
{<br />
double A0= 373.15/(273.15 + celsius);<br />
double SUM = -7.90298 <em > (A0-1);<br />
SUM += 5.02808 </em> log10(A0);<br />
SUM += -1.3816e-7 <em > (pow(10, (11.344</em>(1-1/A0)))-1) ;<br />
SUM += 8.1328e-3 <em > (pow(10,(-3.49149</em>(A0-1)))-1) ;<br />
SUM += log10(1013.246);<br />
double VP = pow(10, SUM-3) <em > humidity;<br />
double T = log(VP/0.61078);   // temp var<br />
return (241.88 </em> T) / (17.558-T);<br />
}<br />
// Algoritimo inteiramente copiado, créditos:<br />
// delta max = 0.6544 wrt dewPoint()<br />
// 5x faster than dewPoint()<br />
// reference: <a rel="nofollow" href="http://en.wikipedia.org/wiki/Dew_point" >http://en.wikipedia.org/wiki/Dew_point</a><br />
double dewPointFast(double celsius, double humidity)<br />
{<br />
double a = 17.271;<br />
double b = 237.7;<br />
double temp = (a <em > celsius) / (b + celsius) + log(humidity/100);<br />
double Td = (b </em> temp) / (a - temp);<br />
return Td;<br />
}
