Savonius_Diametre_Base=100;<br />
Thinkness=4;  
Roller_Diametre_Externe=22;<br />
Roller_Diametre_Interne=8;<br />
Roller_Hauteur=7;  
Motor_Diametre_Central=13;<br />
Motor_Diametre_Holder=3;<br />
Motor_Espacement_Holder=25;  
footer_height=50;  
