Diametre_base=100;                                      //base diameter (D)<br />
ecart=16;                                                       //central Gap - ecart (e)<br />
epaisseur=4;                                                    //thinkness ( base and max'wing)<br />
gorge=0;<br />
hauteur=150;                                                    //height<br />
diametre_pale=(Diametre_base/2)+(ecart/2); //wing diameter  
//try to get 0.166666666666666<br />
echo(&quot;best : 0.15 &lt; &quot;, ecart/Diametre_base ,&quot;&lt; 0.17&quot;);  
Twist=0;                                        //Wings of Fury<br />
n_pale=2;                                       //number of wings -  
//$fa=1;<br />
//$fs=1;  
module savonius(){<br />
base2();<br />
rotor3();<br />
}  
module rotor2(){<br />
for (i=[1:n_pale]){<br />
color([0.5+sin(10<em >i)/2,0,1/i]){<br />
rotate([0,0,i</em>(360/n_pale)]){<br />
translate([-diametre_pale/2+ecart/2,0,epaisseur+gorge]){<br />
linear_extrude(height=hauteur+epaisseur,convexity=100,twist=Twist){<br />
difference(){<br />
circle(r=diametre_pale/2);<br />
circle(r=(diametre_pale/2)-epaisseur/2);<br />
translate([-Diametre_base,0,0])square(Diametre_base*2);<br />
}<br />
}<br />
}<br />
}<br />
}<br />
}<br />
}  
module rotor3(){<br />
for (j=[1:0.3:hauteur]){  
<code >for (i=[1:n_pale]){  
    color([0.5+sin(10*i)/2,0,1/i]){  
        rotate([0,0,i*(360/n_pale)+j]){  
            translate([-diametre_pale/2+ecart/2,0,epaisseur+gorge+j-0.7]){  
                linear_extrude(height=0.3,convexity=100,twist=Twist){  
                    difference(){     
                        circle(r=diametre_pale/2);  
                        circle(r=(diametre_pale/2)-epaisseur/2);  
                        translate([-Diametre_base,0,0])square(Diametre_base*2);  
                    }  
                }  
            }  
        }  
    }  
}  </code>
}<br />
}  
