    translate([0,0,-epaisseur]) cylinder(r=1, h=3*10); //change r as your preference  
    for (i=[1:8]){  
        rotate([0,0,i*(360/8)]) translate([(Diametre_base/2)-14,0,-1]) cylinder(r=10.5,h=epaisseur);  
