// //////////////////////////////<br />
// USER PARAMETERS<br />
// //////////////////////////////
// The diameter of the two shafts<br />
shaft1_dia = 8;<br />
shaft2_dia = 8;
// How much material should be around the holes:<br />
outset_h = 7.0; // minimum mm of material around holes, horizontal<br />
outset_v = 5.0; // vertical outset<br />
roombetween = 1; //mm between holes
// you can reduce the exterior for shaft 2 (the vertical one) by using a negative number here. This is advised to save material, because in the XY-plane the material is very strong compared to the Z-direction<br />
shaft_2_exterior_difference=-4.5; 
// An overhang that your machine prints well without supports<br />
max_overhang = 30; // rule of thumb: 45<br />
chamfer=5.5;
minimize_material = true; // gives the top section a fancy shape, that also reduces the amount of material needed!
